import axios from "../helpers/axios";

import { authContants } from "./constants";

export const login =(user)=>{

    console.log(user);
    return async(dispatch)=>{
        
        const res=await axios.post(`/addmin/signin`,{
            ...user
        })
        dispatch({
            type: authContants.LOGIN_REQUEST,
            payload:{
                ...user
            } 

        });

    }
}
