import { productContants } from "../actions/constants";

const initialState={
    products:[]
};
export default  (state=initialState,action)=>{
    switch(action.type){
        case productContants.GET_ALL_PRODUCTS_SUCCESS:
            state={
                ...state,
                products:action.payload.products
            }
            break;
    }
    return state;
}
