import { categoryContants } from "../actions/constants";

const initState={
    categories:[],
    loading :false,
    error:null
};
const buildNewCategory=(categories,category)=>{
    let myCategories=[];
    for(let cat of categories){
        myCategories.push({
            ...cat,
            children 
        });
    }

}
export default (state=initState,action)=>{
    switch(action.type){
        case categoryContants.GET_ALL_CATEGORIES_SUCCESS:
            state={
                ...state,
                categories:action.payload.categories
            }
            break;
        case categoryContants.ADD_NEW_CATEGORY_REQUEST:
            state={
                ...state,
                loading:true
            }
            break;
        case categoryContants.ADD_NEW_CATEGORY_SUCCESS:
            state={
                ...state,
                categories:buildNewCategory(state.categories,action.payload.category),
                loading:false,
            }
            break;
        case categoryContants.ADD_NEW_CATEGORY_FAILURE:
            state={
                ...initState
            }
            break;
    }
    return state;
}
