import { categoryContants } from "../actions/constants";

const initState = {
    categories: [],
    loading: false,
    error: null
};
const buildNewCategory = (id,categories, category) => {
    let myCategories = [];
    for (let cat of categories) {
        
        if(c&&cat.parentId==id){
            myCategories.push({
                ...cat,
                children: cat.children && cat.children.length > 0 ? buildNewCategory(id,[...cat.children,{
                    _id:category._id,
                    name:category.name,
                    slug:category.slug,
                    parentId: category.parentId,
                    children: category.children
                }], category):[]
            });
        }
        else{
            myCategories.push({
                ...cat,
                children: cat.children && cat.children.length > 0 ? buildNewCategory(id,cat.children, category):[]
            });
        }
        
    }
    return myCategories;

}
export default (state = initState, action) => {
    switch (action.type) {
        case categoryContants.GET_ALL_CATEGORIES_SUCCESS:
            state = {
                ...state,
                categories: action.payload.categories
            }
            break;
        case categoryContants.ADD_NEW_CATEGORY_REQUEST:
            state = {
                ...state,
                loading: true
            }
            break;
        case categoryContants.ADD_NEW_CATEGORY_SUCCESS:
            const category=action.payload.category;
            const updatedCategories=buildNewCategory(category.parentId,state.categories, category);
            console.log('update categories',updatedCategories);
            state = {
                ...state,
                categories:updatedCategories,
                loading: false,
            }
            break;
        case categoryContants.ADD_NEW_CATEGORY_FAILURE:
            state = {
                ...initState
            }
            break;
    }
    return state;
}
