import { categoryContants } from "../actions/constants";

const initState={
    categories:[],
    loading :false,
    error:null
};

export default (state=initState,action)=>{
    switch(action.type){
        case categoryContants.GET_ALL_CATEGORIES_SUCCESS:
            state={
                ...state,
                categories
            }
    }
}
