import { categoryContants } from "../actions/constants";

const initState={
    categories:[],
    loading :false,
    error:null
};

const buildNewCategories=(id,categories,category)=>{
    let myCategories=[];
    for(let cat of  categories){
        if(cat.parentId&&cat.parentId==id){
            myCategories.push({
                ...cat,
                children:cat.children && cat.children.length>0?buildNewCategories(id,[...cat.childen],category):[]
            });
        }
        else{
            myCategories.push({
                ...cat,
                children:cat.children && cat.children.length>0?buildNewCategories(id,cat.childen,category):[]
            });
        }
       
    }
    return myCategories;
}
export default (state=initState,action)=>{
    switch(action.type){
        case categoryContants.GET_ALL_CATEGORIES_SUCCESS:
            state={
                ...state,
                categories:action.payload.categories
            }
            break;
        case categoryContants.ADD_NEW_CATEGORY_REQUEST:
            state={
                ...state,
                loading:true
            }
            break;
        case categoryContants.ADD_NEW_CATEGORY_SUCCESS:
            const updateCategories=buildNewCategories(state.categories,action.payload.category);
            console.log(updateCategories);
            state={
                ...state,
                categories:buildNewCategories(state.categories,action.payload.category),
                loading:false
            }
            break;
        case categoryContants.ADD_NEW_CATEGORY_FAILURE:
            state={
                ...initState
            }
            break;
    }
    return state;
}
